  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	sidebar_position: 2
};
export const metadata = {
  "unversionedId": "tutorial-extras/translate-your-site",
  "id": "tutorial-extras/translate-your-site",
  "isDocsHomePage": false,
  "title": "Translate your site",
  "description": "Let's translate docs/getting-started.md to French.",
  "source": "@site/docs/2-tutorial-extras/translate-your-site.md",
  "sourceDirName": "2-tutorial-extras",
  "slug": "/tutorial-extras/translate-your-site",
  "permalink": "/docs/tutorial-extras/translate-your-site",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/docs/2-tutorial-extras/translate-your-site.md",
  "version": "current",
  "sidebarPosition": 2,
  "frontMatter": {
    "sidebar_position": 2
  },
  "sidebar": "mySiteSidebar",
  "previous": {
    "title": "Manage Docs Versions",
    "permalink": "/docs/tutorial-extras/manage-docs-versions"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Configure i18n',
  id: 'configure-i18n',
  children: []
}, {
  value: 'Translate a doc',
  id: 'translate-a-doc',
  children: []
}, {
  value: 'Start your localized site',
  id: 'start-your-localized-site',
  children: []
}, {
  value: 'Add a Locale Dropdown',
  id: 'add-a-locale-dropdown',
  children: []
}, {
  value: 'Build your localized site',
  id: 'build-your-localized-site',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Let's translate `}<inlineCode parentName="p">{`docs/getting-started.md`}</inlineCode>{` to French.`}</p>
    <h2 {...{
      "id": "configure-i18n"
    }}>{`Configure i18n`}</h2>
    <p>{`Modify `}<inlineCode parentName="p">{`docusaurus.config.js`}</inlineCode>{` to add support for the `}<inlineCode parentName="p">{`fr`}</inlineCode>{` locale:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js",
        "metastring": "title=\"docusaurus.config.js\"",
        "title": "\"docusaurus.config.js\""
      }}>{`module.exports = {
  i18n: {
    defaultLocale: 'en',
    locales: ['en', 'fr'],
  },
};
`}</code></pre>
    <h2 {...{
      "id": "translate-a-doc"
    }}>{`Translate a doc`}</h2>
    <p>{`Copy the `}<inlineCode parentName="p">{`docs/getting-started.md`}</inlineCode>{` file to the `}<inlineCode parentName="p">{`i18n/fr`}</inlineCode>{` directory:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`mkdir -p i18n/fr/docusaurus-plugin-content-docs/current/

cp docs/getting-started.md i18n/fr/docusaurus-plugin-content-docs/current/getting-started.md
`}</code></pre>
    <p>{`Translate `}<inlineCode parentName="p">{`i18n/fr/docusaurus-plugin-content-docs/current/getting-started.md`}</inlineCode>{` in French.`}</p>
    <h2 {...{
      "id": "start-your-localized-site"
    }}>{`Start your localized site`}</h2>
    <p>{`Start your site on the French locale:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`npm run start -- --locale fr
`}</code></pre>
    <p>{`Your localized site is accessible at `}<inlineCode parentName="p">{`http://localhost:3000/fr/`}</inlineCode>{` and the `}<inlineCode parentName="p">{`Getting Started`}</inlineCode>{` page is translated.`}</p>
    <div {...{
      "className": "admonition admonition-warning alert alert--danger"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M5.05.31c.81 2.17.41 3.38-.52 4.31C3.55 5.67 1.98 6.45.9 7.98c-1.45 2.05-1.7 6.53 3.53 7.7-2.2-1.16-2.67-4.52-.3-6.61-.61 2.03.53 3.33 1.94 2.86 1.39-.47 2.3.53 2.27 1.67-.02.78-.31 1.44-1.13 1.81 3.42-.59 4.78-3.42 4.78-5.56 0-2.84-2.53-3.22-1.25-5.61-1.52.13-2.03 1.13-1.89 2.75.09 1.08-1.02 1.8-1.86 1.33-.67-.41-.66-1.19-.06-1.78C8.18 5.31 8.68 2.45 5.05.32L5.03.3l.02.01z"
              }}></path></svg></span>{`warning`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`In development, you can only use one locale at a same time.`}</p></div></div>
    <h2 {...{
      "id": "add-a-locale-dropdown"
    }}>{`Add a Locale Dropdown`}</h2>
    <p>{`To navigate seamlessly across languages, add a locale dropdown.`}</p>
    <p>{`Modify the `}<inlineCode parentName="p">{`docusaurus.config.js`}</inlineCode>{` file:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js",
        "metastring": "title=\"docusaurus.config.js\"",
        "title": "\"docusaurus.config.js\""
      }}>{`module.exports = {
  themeConfig: {
    navbar: {
      items: [
        // highlight-start
        {
          type: 'localeDropdown',
        },
        // highlight-end
      ],
    },
  },
};
`}</code></pre>
    <p>{`The locale dropdown now appears in your navbar:`}</p>
    <p><img alt={"Locale Dropdown"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!/Users/sebastienlorber/Desktop/projects/docusaurus/packages/docusaurus-init/templates/classic/static/img/tutorial/localeDropdown.png").default} /></p>
    <h2 {...{
      "id": "build-your-localized-site"
    }}>{`Build your localized site`}</h2>
    <p>{`Build your site for a specific locale:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`npm run build -- --locale fr
`}</code></pre>
    <p>{`Or build your site to include all the locales at once:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`npm run build
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
