  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	sidebar_position: 1
};
export const metadata = {
  "unversionedId": "tutorial-extras/manage-docs-versions",
  "id": "version-2.0/tutorial-extras/manage-docs-versions",
  "isDocsHomePage": false,
  "title": "Manage Docs Versions",
  "description": "Docusaurus can manage multiple versions of your docs.",
  "source": "@site/versioned_docs/version-2.0/tutorial-extras/manage-docs-versions.md",
  "sourceDirName": "tutorial-extras",
  "slug": "/tutorial-extras/manage-docs-versions",
  "permalink": "/docs/tutorial-extras/manage-docs-versions",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/versioned_docs/version-2.0/tutorial-extras/manage-docs-versions.md",
  "version": "2.0",
  "sidebarPosition": 1,
  "frontMatter": {
    "sidebar_position": 1
  },
  "sidebar": "version-2.0/tutorialSidebar",
  "previous": {
    "title": "Congratulations!",
    "permalink": "/docs/tutorial-basics/congratulations"
  },
  "next": {
    "title": "Translate your site",
    "permalink": "/docs/tutorial-extras/translate-your-site"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Create a docs version',
  id: 'create-a-docs-version',
  children: []
}, {
  value: 'Add a Version Dropdown',
  id: 'add-a-version-dropdown',
  children: []
}, {
  value: 'Update an existing version',
  id: 'update-an-existing-version',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Docusaurus can manage multiple versions of your docs.`}</p>
    <h2 {...{
      "id": "create-a-docs-version"
    }}>{`Create a docs version`}</h2>
    <p>{`Release a version 1.0 of your project:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`npm run docusaurus docs:version 1.0
`}</code></pre>
    <p>{`The `}<inlineCode parentName="p">{`docs`}</inlineCode>{` folder is copied into `}<inlineCode parentName="p">{`versioned_docs/version-1.0`}</inlineCode>{` and `}<inlineCode parentName="p">{`versions.json`}</inlineCode>{` is created.`}</p>
    <p>{`Your docs now have 2 versions:`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`1.0`}</inlineCode>{` at `}<inlineCode parentName="li">{`http://localhost:3000/docs/`}</inlineCode>{` for the version 1.0 docs`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`current`}</inlineCode>{` at `}<inlineCode parentName="li">{`http://localhost:3000/docs/next/`}</inlineCode>{` for the `}<strong parentName="li">{`upcoming, unreleased docs`}</strong></li>
    </ul>
    <h2 {...{
      "id": "add-a-version-dropdown"
    }}>{`Add a Version Dropdown`}</h2>
    <p>{`To navigate seamlessly across versions, add a version dropdown.`}</p>
    <p>{`Modify the `}<inlineCode parentName="p">{`docusaurus.config.js`}</inlineCode>{` file:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js",
        "metastring": "title=\"docusaurus.config.js\"",
        "title": "\"docusaurus.config.js\""
      }}>{`module.exports = {
  themeConfig: {
    navbar: {
      items: [
        // highlight-start
        {
          type: 'docsVersionDropdown',
        },
        // highlight-end
      ],
    },
  },
};
`}</code></pre>
    <p>{`The docs version dropdown appears in your navbar:`}</p>
    <p><img alt={"Docs Version Dropdown"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!/Users/sebastienlorber/Desktop/projects/docusaurus/packages/docusaurus-init/templates/classic/static/img/tutorial/docsVersionDropdown.png").default} /></p>
    <h2 {...{
      "id": "update-an-existing-version"
    }}>{`Update an existing version`}</h2>
    <p>{`It is possible to edit versioned docs in their respective folder:`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`versioned_docs/version-1.0/hello.md`}</inlineCode>{` updates `}<inlineCode parentName="li">{`http://localhost:3000/docs/hello`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`docs/hello.md`}</inlineCode>{` updates `}<inlineCode parentName="li">{`http://localhost:3000/docs/next/hello`}</inlineCode></li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
